import { Component } from '@angular/core';
import { BaseService } from '../app.service';
import { TestService, DataSharingService } from '../test.service';
import { Book } from '../books/book.service';
@Component({
  selector: 'app-basket',
  templateUrl: './basket.component.html',
  styleUrls: ['./basket.component.scss']
})
export class BasketComponent {



  books: Book[] = [];

  // constructor(public testService: TestService){ }

  // ngOnInit(){

  //   this.books.push(this.testService.myData)

  // }

  constructor(private dataSharingService: DataSharingService) {}

  ngOnInit() {
    this.dataSharingService.getDataArray().subscribe((dataArray) => {
      this.books = dataArray;
    });
  }



}
