  // }

  constructor(private dataSharingService: DataSharingService) {}

  passData() {
    const data = { /* your data */ };
    this.dataSharingService.pushDataToArr(data);
  }


}
<div>
   Book id: {{ bookData.id }} <br>
   Book title: {{ bookData.title }} <br>
   Book publication year: {{ bookData.publicationYear }} <br>
   Book author and author name: {{ bookData.author }} {{ bookData.authorName }} <br>
   Book description: {{ bookData.description }}
</div>
<button (click)="addToBasket(bookData)">Add to basket</button>
