import { Book } from "../book.service";
import { BaseService } from 'src/app/app.service';

@Component({
  selector: 'app-book-item',
  templateUrl: './book-item.component.html',
  styleUrls: ['./book-item.component.scss']
})

export class BookItemComponent {
  @Input() bookData: Book
  mess:string
  constructor(private data: BaseService){}

  ngOnInit(){
    this.data.currMess.subscribe(mess => this.mess = mess)
  }

  addToBasket(bookData) {
    this.data.changeMess
    console.log(bookData)
  }
}
<div>
   Book id: {{ bookData.id }} <br>
   Book title: {{ bookData.title }} <br>
   Book publication year: {{ bookData.publicationYear }} <br>
   Book author and author name: {{ bookData.author }} {{ bookData.authorName }} <br>
   Book description: {{ bookData.description }}
</div>
<button (click)="addToBasket(bookData)">Add to basket</button>
